function getViews(body, name) {
	const views = body.data.tableSchemas.find(schema => schema.name === name).views;
	if (!views) return;

	return views.map(view => ({label: view.name, value: view.id}));
}

applySort.code:
function applySort(qs, sort) {
	if (!sort || !Array.isArray(sort)) return qs;

	sort.forEach((item, idx) => {
		qs[`sort[${idx}][field]`] = item.field;
		qs[`sort[${idx}][direction]`] = item.direction;
	});

	return qs;
}

getSchema.code:
function getSchema(body, name, _for, _filterIn) {
	const columns = body.data.tableSchemas.find(schema => schema.name === name).columns;
	if (!columns) return;

	if (_filterIn && !Array.isArray(_filterIn)) {
		_filterIn = [_filterIn];
	}

	if (Array.isArray(_filterIn)) {
		_filterIn = _filterIn.map(item => item.toLowerCase());
	}

	if (!_filterIn || !_filterIn.length) {
		_filterIn = null;
	}

	const result = columns.map(col => {
		const field = {
			name: col.name,
			label: col.label,
			type: "text"
		};

		if (_filterIn && !_filterIn.includes(col.type.toLowerCase())) {
			return;
		}

		switch(col.type) {
			case "multilineText":
				field.multiline = true;
				break;
			case "multipleAttachment":
				// For interface
				if (_for === 'interface') {
					field.type = "array";
					field.spec = [{
						name: "url",
						type: "url"
					}, {
						name: "filename",
						type: "text"
					}, {
						name: "size",
						type: "number"
					}, {
						name: "type",
						label: "MIME type",
						type: "text"
					}];
					break;
				}

				// For expect
				field.type = "array";
				field.editable = true;
				field.spec = [{
					name: "url",
					label: "File URL",
					type: "url",
					help: "Airtable will download the file at the given url and keep its own copy of it.",
					required: true
				}, {
					name: "filename",
					label: "File name",
					type: "filename",
					help: "If empty, Airtable will generate the name automatically."
				}];
				break;
			case "checkbox":
				field.type = "boolean";
				break;
			case "multiSelect":
				// For interface
				if (_for === 'interface') {
					field.type = "array";
					field.spec = { type: "text" };
					field.editable = true;
					break;
				}

				// For expect
				field.multiple = true;
				// Switch fall through is made on purpose because multi select and select have common processing logic in expect.
			case "select":
				// For interface
				if (['interface', 'rpc'].includes(_for)) break; // Keep select field as text for interface and rpc

				// For expect
				field.type = "select";
				field.editable = true;
				field.options = col.typeOptions.choiceOrder.map(choiceId => ({
					label: col.typeOptions.choices[choiceId].name,
					value: col.typeOptions.choices[choiceId].name
				}));
				break;
			case "collaborator":
				// For interface
				if (['interface', 'rpc'].includes(_for)) {
					field.type = "collection";
					field.spec = [{
						name: "email",
						type: "email"
					}, {
						name: "name",
						type: "text"
					}];
					break;
				}

				field.type = "collection";
				field.spec = [{
					name: "email",
					type: "email",
					label: "Email",
					help: "Email that uniquely identifies a user in Airtable who this base is shared with"
				}];
				break;
			case "multiCollaborator":
				// For interface
				if (['interface', 'rpc'].includes(_for)) {
					field.type = "array";
					field.spec = [{
						name: "email",
						type: "email"
					}, {
						name: "name",
						type: "text"
					}];
					break;
				}

				field.type = "array";
				field.spec = [{
					name: "email",
					type: "email",
					label: "Email",
					help: "Email that uniquely identifies a user in Airtable who this base is shared with"
				}];
				break;
			case "date":
				field.type = "date";
				field.time = col.typeOptions.isDateTime;
				break;
			case "text":
				if (col.typeOptions && col.typeOptions.validatorName === "email") field.type = "email";
				if (col.typeOptions && col.typeOptions.validatorName === "url") field.type = "url";
				break;
			case "number":
				field.type = "number";
				if (col.typeOptions.format === "integer") field.type = "integer";
				if (col.typeOptions.format === "currency") field.help = `Currency: ${col.typeOptions.symbol}`;
				if (col.typeOptions.format === "percent") field.help = "Percent";
				if (!col.typeOptions.negative) field.validate = { min: 0 };
				break;
			case "autoNumber":
			case "formula":
				if (['interface', 'rpc'].includes(_for)) break;
				return;
			case "foreignKey":
				field.type = "array";
				field.editable = true;
				field.help = "Array of Airtable record Ids";
				field.spec = { type: "text" };
				break;
			case "barcode":
				field.type = "collection";
				field.spec = [{
					name: "text",
					type: "text"
				}];
				break;
			case "rating":
				field.type = "uinteger";
				field.validate = {
					min: 1,
					max: col.typeOptions.max
				};
				break;
			case "lookup":
				if (!['interface', 'rpc'].includes(_for)) return;
				field.type = "array";
				field.spec = { type: "any" };
				break;
		}

		return field;
	}).filter(field => !!field);

	if (_for === 'interface') {
		result.unshift({
			name: 'id',
			type: 'text'
		}, {
			name: 'createdTime',
			type: 'date'
		});
	} else if (_for === 'rpc') {
		return result
			.map(item => ({label: item.name, value: item.name}));
	}

	return result;
}

getTables.code:
function getTables(body) {
	const ma = String(body).match(/window.initData = (\{.+\});\n/);
	if (!ma) return [];
	const data = JSON.parse(ma[1]);
	return Object.keys(data.rawApplications).map(appid => ({
		label: data.rawApplications[appid].name,
		options: data.rawApplications[appid].visibleTableOrder.map(tabid => ({
			label: data.rawTables[tabid].name,
			value: `${appid}/${data.rawTables[tabid].name}`
		}))
	}));
}

wrapOutput.code:
function wrapOutput(output, _for) {
	if (['interface', 'rpc'].includes(_for)) return output;
	return [
		{
			"name": "record",
			"spec": output,
			"type": "collection",
			"label": "Record"
		}
	]
}

encodeTable.code:
function encodeTable(str) {
	const app = iml.substring(str, 0, iml.indexOf(str, '/'));
	const table = iml.substring(str, iml.indexOf(str, '/') + 1);
	return `${app}/${iml.encodeURL(table)}`;
}

getUserName.code:
function getUserName(body) {
const ma = String(body).match(/window.initData = (\{.+\});\n/);
	if (!ma) return [];
	const data = JSON.parse(ma[1]);
	const user = data.rawUsers[data.sessionUserId] || data.rawUsers[Object.keys(data.rawUsers)[0]];
	return user.name;
}

