function removeNulls(record) {
	Object.keys(record).forEach(key => {
		if (record[key] === null) delete record[key];
		else if (Array.isArray(record[key]) && !record[key].length) delete record[key];
		else if (record[key].text === null) delete record[key];
		else if (record[key].email === null) delete record[key];
	});
	return record;
}

prepareRecord.code:
function prepareRecord(record) {
	metadata.expect.filter(param => param.name === 'record')[0].spec
		.filter(param => param.type === 'date' && param.time === false && record[param.name] != null)
		.forEach(param => record[param.name] = iml.formatDate(record[param.name], 'YYYY-MM-DD', environment.timezone))

	return record;
}

processRecord.code:
function processRecord(record) {
	return Object.assign(record.fields, {id: record.id, createdTime: record.createdTime});
}

getCsrfFromBody.code:
function getCsrfFromBody(body) {
  let csrfNameIdx = body.indexOf('name="_csrf"');
  let input = body.substring(body.lastIndexOf('<', csrfNameIdx), body.indexOf('>', csrfNameIdx));
  let [fullMatch, csrf] = input.match(/value="(.+)"/i);
  return csrf;
}
