function parseTools(paramIn) {
    if (!paramIn || !Array.isArray(paramIn))
        return;

    let param;
    try {
        param = JSON.parse(JSON.stringify(paramIn))
    } catch (error) {
        new TypeError('Invalid format for additional parameters. ' + error)
    }

    return param.map(i => {
        if (i.type === 'tools') {
            i.value = (i.value || []).map(j => {
                if (!(j instanceof Object)) {
                    try {
                        return JSON.parse(j)
                    } catch (error) {
                        throw TypeError('Invalid JSON value for tools')
                    }
                }
                return j;
            })
        }
        return i
    })
}

parseTools.test:
it('Should parse the inner JSON for items with type === tools, without modifying the input.', () => {
	const input = [
		{
			"key": "test",
			"type": "tools",
			"value": [
				'{"x": 123}',
				'{"y": 456}'
			]
		},
		{
			"key": "test",
			"type": "tools",
			"value": [
				'{"a": "bla"}',
				'{"b": "123"}'
			]
		}
	];

	const inputOriginal = [
		{
			"key": "test",
			"type": "tools",
			"value": [
				'{"x": 123}',
				'{"y": 456}'
			]
		},
		{
			"key": "test",
			"type": "tools",
			"value": [
				'{"a": "bla"}',
				'{"b": "123"}'
			]
		}
	];

	const expected = [
		{
			key: 'test',
			type: 'tools',
			value: [{ x: 123 }, { y: 456 }]
		},
		{
			key: 'test',
			type: 'tools',
			value: [{ a: 'bla' }, { b: '123' }]
		}
	];

	assert.deepStrictEqual(parseTools(input), expected);
	assert.deepStrictEqual(input, inputOriginal);
})

