        body.content.forEach(item => {
            if(item.text) {
                textResponse += `${item.text}\n`;
            }
            if(item.type === 'tool_use') {
                tools[item.name] = item;
            }
        });


        return {
            textResponse: textResponse || undefined,
            ...body,
            tools: Object.keys(tools).length === 0 ? undefined : tools
        };
    }
    return body;
}

