function formatAttachments(messages) {

/**
 * Returns a list of formatted messages
 * @param {array || undefined} [messages]
 * @return {"role": string, "content": string|{"text": string|undefined, "type": string}[]} - if the role is user and content is text
 * @return {"role": string, "content": {"source": {"type": string, "media_type": string, "data": string}, "type": string}[]} - if the role is user and content is file
 * @return {"role": string, "content": string} - if the role is assistant or system
 */

    if (!messages) return messages
    return messages.map((message) => ({
        ...message,
        content: Array.isArray(message.content) ? message.content.map((i) => ({
            type: i.type,
            ...(i.text && { text: i.text }),
            ...((i.source || i.type === 'document') && {
                source: {
                    type: 'base64',
                    media_type: iml.mime(i.source?.filename ?? i.pdfFileName),
                    data: iml.base64(i.source?.data ?? i.pdfFileData)
                }
            })
        })) : message.content
    }))
}

