function formatInput(obj) {
	if(obj === null || obj === undefined) return undefined
	if(Object.keys(obj)[0] === null || Object.keys(obj)[0] === undefined) return undefined
	let result = {}
	Object.keys(obj).map(item => {
		if(item.split('[')[1].slice(0, -1) === 'STRING' || item.split('[')[1].slice(0, -1) === 'DATETIME') {
			result[item.split('[')[0]] = {
				type: item.split('[')[1].slice(0, -1),
				string: obj[item]
			}	
		}
		if(item.split('[')[1].slice(0, -1) === 'NUMBER') {
			result[item.split('[')[0]] = {
				type: item.split('[')[1].slice(0, -1),
				number: obj[item]
			}	
		}
		if(item.split('[')[1].slice(0, -1) === 'BOOLEAN') {
			result[item.split('[')[0]] = {
				type: item.split('[')[1].slice(0, -1),
				boolean: obj[item]
			}	
		}
		if(item.split('[')[1].slice(0, -1) === 'LIST') {
			result[item.split('[')[0]] = {
				type: item.split('[')[1].slice(0, -1),
				list: obj[item]
			}	
		}
		if(item.split('[')[1].slice(0, -1) === 'DICTIONARY') {
			result[item.split('[')[0]] = {
				type: item.split('[')[1].slice(0, -1),
				dictionary: obj[item]
			}	
		}
		if(item.split('[')[1].slice(0, -1) === 'TABLE') {
			result[item.split('[')[0]] = {
				type: item.split('[')[1].slice(0, -1),
				table: obj[item]
			}	
		}
	})
	return result
}

