formatVariables.code:
function formatVariables(object) {
	if(object === null || object === undefined) return undefined
	if(Object.keys(object).length === 0) return []

	let result = []

	Object.keys(object).map(item => {
		result.push({
			variable: item,
			...object[item]
		})
	})
	return result.map(item => {
		if(item.type === 'DATETIME') item.string = iml.parseDate(item.string)
		if(item.type === 'LIST') {
			item.list = item.list.map(item_child => {
				if(item_child.type === 'DATETIME') item_child.string = iml.parseDate(item_child.string)
				return item_child
			})
		}
		if(item.type === 'DICTIONARY') {
			item.dictionary = item.dictionary.map(item_child_dictionary => {
				if(item_child_dictionary.value.type === 'DATETIME') item_child_dictionary.value.string = iml.parseDate(item_child_dictionary.value.string)
				return item_child_dictionary
			})
		}
		if(item.type === 'TABLE') {
			item.table.rows = item.table.rows.map(item_table => {
				item_table.values.map(item_table_child => {
					if(item_table_child.type === 'DATETIME') item_table_child.string = iml.parseDate(item_table_child.string)
					return item_table_child
				})
				return item_table
			})
		}
		return item
	})

}

