addMime.code:
function addMime(headers, mime) {
	if (!mime) return headers;

	try {
		const found = Object.keys(headers).map(key => String(key).toLowerCase()).some(key => key === 'content-type');
		if (!found) headers['content-type'] = mime;
	} catch (ex) {}

	return headers;
}

stripSlash.code:
function stripSlash(text) {
	if (typeof text != 'undefined' && /\/$/.test(text)) {
		return text.substring(0, text.length - 1);
	}
	return text;
}

normalizePrefix.code:
function normalizePrefix(prefix) {
	prefix = prefix || '';
	if (/^\//.test(prefix)) prefix = prefix.substr(1); // Ensure prefix doesn't start with /
	if (!/\/$/.test(prefix)) prefix = prefix + '/'; // Ensure prefix ends with /
	if (prefix === '/') prefix = '';
	return prefix;
}

stripLeadingSlash.code:
function stripLeadingSlash(text) {
    if (typeof text === 'string') {
		return text.replace(/^\/+/, '');
	}
	return text;
}

stripLeadingSlash.test:
it('should remove a single leading slash from a string', () => {
	const result = stripLeadingSlash('/path/to/resource');
	assert.strictEqual(result, 'path/to/resource');
});

it('should remove multiple leading slashes', () => {
	const result = stripLeadingSlash('///path/to/resource');
	assert.strictEqual(result, 'path/to/resource');
});

it('should return the string unchanged if it has no leading slash', () => {
	const result = stripLeadingSlash('noleadingslash');
	assert.strictEqual(result, 'noleadingslash');
});
