function deploymentFiltering(deployments, modelType) {
	if (!Array.isArray(deployments))
		return []

	return deployments.filter((d) => {
		if (d.properties.model.format !== 'OpenAI') return false;
		switch (modelType) {
			case 'chat':
				return d.properties.capabilities.chatCompletion;
			case 'whisper':
				return d.properties.capabilities.audio;
			case 'dalle':
				return d.properties.capabilities.imageGenerations;
		}
		return true;
	}).map((d) => {
		return {
			label: `${d.name} (${d.properties.model.name})`,
			value: d.name
		}
	});
}

