function parseGenerateImgResponse(res, response_format) {
    if (typeof res !== 'object')
        throw TypeError('"res" argument is expected to be an object.');
    if (Array.isArray(res))
        throw TypeError('"res" argument is expected to be an object.');
    if (!res.hasOwnProperty('data'))
        throw TypeError('"data" property is missing in the response.');
    if (!Array.isArray(res.data))
        throw TypeError('"data" is expected to be an array.');

    const data = res.data.find((item, idx) => {
        if (typeof item !== 'object')
            throw TypeError(`data[${idx}] is not an object.`);
        if (response_format === 'imageFile' && !item.hasOwnProperty('b64_json'))
            throw TypeError(`data[${idx}] is missing "b64_json" property.`);

        if (response_format === 'url' && !item.hasOwnProperty('url'))
            throw TypeError(`data[${idx}] is missing "url" property.`);

        return true;
    });

    const { b64_json, revised_prompt, url } = data

    return {
        created: res.created,
        revised_prompt,
        url,
        filename: b64_json ? 'output.png' : undefined,
        data: b64_json ? iml.toBinary(b64_json, 'base64') : undefined
    };
}

