transformDate.code:
function transformDate(obj) {
	if (obj.created_at) {obj.created_at = iml.parseDate(obj.created_at);}
	if (obj.start_time) {obj.start_time = new Date(obj.start_time);}
	if (obj.timestamp) {obj.timestamp = new Date(obj.timestamp);}
	return obj;
}
