function VerifySignature(body,headers,token) {
	if (token === null) 
		return true;
	signature = headers['x-chatworkwebhooksignature']
	hash = iml.sha256(JSON.stringify(body),'base64',iml.toBinary(token,'base64'),'binary');
	return hash==signature;
}

