function removeNulls(obj) {
    let output = null;
    if (obj) {
	    if (Array.isArray(obj)) {
			output = null;
	    	for (let index in obj) {
	    		let value = obj[index];
				if (Array.isArray(value) && value.length > 0) value = iml.removeNulls(value);
				else if (typeof value === 'object' && !(value instanceof Date)) value = iml.removeNulls(value);
	    		if (value != null) {
	    			if (!output) output = [];
					output.push(value);
				}
	    	}
		}
	    else if (typeof obj === 'object' && !(obj instanceof Date)) {
	    	output = null;
	    	for (let key in obj) {
	    		let value = obj[key];
				if (Array.isArray(value) && value.length > 0) value = iml.removeNulls(value);
				else if (typeof value === 'object' && !(value instanceof Date)) value = iml.removeNulls(value);
	    		if (value != null) {
	    			if (!output) output = {};
					output[key] = value;
				}
	    	}
	    }
	    else output = obj;
	}
    return output;
}

