function mapEventsProperties(events) {
	if (events) {
		let eL = events.length;
		for (let i = 0; i < eL; i++) {
			properties = events[i].properties;
			if (properties) {
				let mappedProperties = {};
				let pL = properties.length;
				for (let j = 0; j < pL; j++) {
					let k = properties[j].key;
					let v = properties[j].value;
					mappedProperties [k] = v;
				}
				events[i].properties = mappedProperties;
			}
		}
	}
	return events;
}

