function mapArrayToCommaSeparatedText(array) {
	let output = null;
	if (array && Array.isArray(array) && array.length > 0) {
		output = "";
		for (let i=0,l=array.length;i<l;i++) {
			if (i > 0) output += ",";
			output += array[i];
		}
	}
	return output;
}
