verifyEmailFile.apiJsonc:
[
	{
		"url": "/verifyApiFile",
		"method": "POST",
		"type": "multipart/form-data",
		"qs": {
			"filename": "{{parameters.fileName}}"
		},
		"body": {
			"file_contents": {
				"value": "{{parameters.fileData}}",
				"options": {
					"filename": "{{parameters.fileName}}"
				}
			}
		},
		"response": {
			"type": "text",
			"valid": "{{body !== 'no_credit'}}",
			"error": {
				"type": "RuntimeError",
				"message": "Unfortunately you don't have enough credit to process your list."
			},
			"temp": {
				"listId": "{{body}}"
			},
			"output": null
		}
	},
	{
		"url": "/getApiFileInfo",
		"method": "GET",
		"qs": {
			"id": "{{temp.listId}}"
		},
		"repeat": {
			"condition": "{{temp.status === 'new' || temp.status === 'parsing' || temp.status === 'waiting' || temp.status === 'progress'}}",
			"delay": 10000,
			"limit": 10
		},
		"response": {
			"type": "text",
			"valid": "{{temp.status !== 'failed'}}",
			"error": {
				"type": "DataError",
				"message": "Verifying is failed"
			},
			"temp": {
				"status": "{{getItemFromResponse(body, 'stat')}}"
			},
			"output": null
		}
	},
	{
		"condition": "{{temp.status === 'finished'}}",
		"url": "/getApiFileInfo",
		"method": "GET",
		"qs": {
			"id": "{{temp.listId}}"
		},
		"response": {
			"output":{
				"FileURL": "{{getItemFromResponse(body, 'linkAll')}}"
			}
		}
	}
]

