getItemFromResponse.code:
function getItemFromResponse(obj, itemName) {
	const [id, filename, unique, lines, lines_processed, status, timestamp, link_ok, link_all] = obj.split('|');
	switch (itemName) {
    case 'stat':
		return status;
    case 'linkAll':
		return link_all;
	}
}
