parseListFields.code:
function parseListFields(list) {
  if (list.data) {
    return null;
  }

  const listFields = list.fields
    .filter(field => field.tag !== 'EmailAddress')
    .map(field => {
      if (field.tag === 'FirstName' || field.tag === 'LastName') {
        field.label = field.label
          .split(' ')
          .map(word => iml.capitalize(word))
          .join(' ');
      }

      return iml.convertToFieldTypeIntegromat(field);
    });

  return {
    name: 'fields',
    type: 'collection',
    spec: listFields,
    label: 'Fields'
  };
}

