}

parseDateFromISOFormat.code:
function parseDateFromISOFormat(item) {
  if (item.created_at) {
    item.created_at = iml.parseDate(item.created_at);
  }
  if (item.sent_at) {
    item.sent_at = iml.parseDate(item.sent_at);
  }

  return item;
}

convertToFieldTypeIntegromat.code:
function convertToFieldTypeIntegromat(field) {
  return {
    name: field.tag,
    type: field.type.toLowerCase(),
    label: field.label
  };
}
