facebook-business-extension.apiJsonc:
{
    "authorize": {
        "temp": {
            "scope": "{{join(oauth.scope, ',')}}",
            "externalBusinessId": "{{toString(organization.id)}}{{if(parameters.externalBusinessId, ':' + parameters.externalBusinessId)}}",
            "extras": {
                "setup": {
                    "external_business_id": "{{toString(organization.id)}}{{if(parameters.externalBusinessId, ':' + parameters.externalBusinessId)}}",
                    "timezone": "{{parameters.timezone}}",
                    "currency": "{{parameters.currency}}",
                    "business_vertical": "MEASUREMENT_AND_OPTIMIZATION",
                    "channel": "CONVERSIONS_API"
                },
                "business_config": {
                    "business": {
                        "name": "{{parameters.businessName}}"
                    }
                },
                "repeat": false
            }
        },
        "url": "https://facebook.com/dialog/oauth",
        "qs": {
            "client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
            "display": "page",
            "redirect_uri": "{{oauth.redirectUri}}",
            "response_type": "code",
            "scope": "{{temp.scope}}",
            "extras": "{{createJSON(temp.extras)}}"
        },
        "response": {
            "temp": {
                "code": "{{query.code}}"
            }
        }
    },
    "token": {
        "url": "https://graph.facebook.com/v22.0/oauth/access_token",
        "method": "POST",
        "type": "urlencoded",
        "body": {
            "code": "{{temp.code}}",
            "client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
            "grant_type": "authorization_code",
            "redirect_uri": "{{oauth.redirectUri}}",
            "client_secret": "{{ifempty(parameters.clientSecret, common.clientSecret)}}"
        },
        "response": {
            "data": {
                "accessToken": "{{body.access_token}}",
                "scope": "{{temp.scope}}",
                "externalBusinessId": "{{temp.externalBusinessId}}"
            }
        },
        "log": {
            "sanitize": [
                "request.body.code",
                "request.body.client_secret",
                "response.body.access_token"
            ]
        }
    },
    "info": [
        {
            "url": "https://graph.facebook.com/v22.0/fbe_business/fbe_installs",
            "method": "GET",
            "qs": {
                "access_token": "{{connection.accessToken}}",
                "fbe_external_business_id": "{{connection.externalBusinessId}}"
            },
            "response": {
                "type": "json",
                "data": {
                    "businessManagerId": "{{body.data[1].business_manager_id}}",
                    "pixelId": "{{body.data[1].pixel_id}}"
                }
            },
            "log": {
                "sanitize": [
                    "request.qs.access_token"
                ]
            }
        },
        {
            "url": "https://graph.facebook.com/v22.0/{{connection.businessManagerId}}/access_token",
            "method": "POST",
            "type": "multipart/form-data",
            "body": {
                "app_id": "{{ifempty(parameters.clientId, common.clientId)}}",
                "scope": "{{connection.scope}}",
                "access_token": "{{connection.accessToken}}",
                "fbe_external_business_id": "{{connection.externalBusinessId}}"
            },
            "response": {
                "type": "json",
                "error": {
                    "message": "[{{statusCode}}] {{body.error.message}}{{body.error.error_user_title}}\n{{body.error.error_user_msg}}"
                },
                "data": {
                    "systemUserToken": "{{body.access_token}}"
                }
            },
            "log": {
                "sanitize": [
                    "request.body.access_token",
                    "response.body.access_token"
                ]
            }
        },
        {
            "url": "https://graph.facebook.com/v22.0/me",
            "headers": {
                "Authorization": "Bearer {{connection.systemUserToken}}"
            },
            "response": {
                "type": "json",
                "uid": "{{body.id}}",
                "metadata": {
                    "type": "text",
                    "value": "{{body.name}} ({{connection.externalBusinessId}})"
                }
            },
            "log": {
                "sanitize": [
                    "request.headers.authorization"
                ]
            }
        }
    ],
    "invalidate": {
        "url": "https://graph.facebook.com/v22.0/fbe_business/fbe_installs",
        "method": "DELETE",
        "qs": {
            "fbe_external_business_id": "{{connection.externalBusinessId}}",
            "access_token": "{{connection.accessToken}}"
        },
        "response": {
            "valid": "{{body.success}}",
            "type": "json"
        },
        "log": {
            "sanitize": [
                "request.qs.access_token"
            ]
        }
    }
}

