function createBody(body) {
	return (body.data || []).map(event => {
		if (typeof event.event_time.getTime === 'function') {
			event.event_time = iml.formatDate(event.event_time, "X");
		} else {
			event.event_time = Math.round(Date.now() / 1000);
		}

		const ud = event.user_data;
		if ((ud.client_ip_address && !ud.client_user_agent) || (!ud.client_ip_address && ud.client_user_agent)) {
			throw new Error('You must provide both Client User Agent and Client IP Address.');
		}
		if (ud.em) ud.em = iml.sha256(String(ud.em).trim().toLowerCase());
		if (ud.ph) ud.ph = iml.sha256(String(ud.ph).trim().toLowerCase().replace(/[^0-9]/g, ''));
		if (ud.ge) ud.ge = iml.sha256(String(ud.ge).trim().toLowerCase());
		if (ud.db) ud.db = iml.sha256(iml.formatDate(ud.db, 'YYYYMMDD'));
		if (ud.ln) ud.ln = iml.sha256(String(ud.ln).trim().toLowerCase());
		if (ud.fn) ud.fn = iml.sha256(String(ud.fn).trim().toLowerCase());
		if (ud.ct) ud.ct = iml.sha256(String(ud.ct).trim().toLowerCase().replace(/[^a-z0-9]/g, ''));
		if (ud.st) ud.st = iml.sha256(String(ud.st).trim().toLowerCase());
		if (ud.zp) ud.zp = iml.sha256(String(ud.zp).trim().toLowerCase().replace(/[^0-9]/g, ''));
		if (ud.country) ud.country = iml.sha256(String(ud.country).trim().toLowerCase());
		if (ud.external_id) ud.external_id = iml.sha256(String(ud.external_id).trim().toLowerCase());

		let custom_data = {};
		if (Array.isArray(event.custom_data) && event.custom_data.length > 0) {
			Object.assign(custom_data, iml.toCollection(event.custom_data, "name", "value"));
		}

		Object.keys(event).filter(key => /^custom_data_/.test(key)).forEach(key => {
			custom_data[key.substring(12)] = event[key];
			delete event[key];
		});

		event.custom_data = custom_data;

		if (body.ldu) {
			event.data_processing_options = ['LDU'];
			event.data_processing_options_country = 1;
			event.data_processing_options_state = 1000;
		}

		return event;
	});
}
