omit.code:
function omit(collection, ...parameters) {
    const result = {};

    for (let key in collection) {
        if (!parameters.includes(key)) {
            result[key] = collection[key];
        }
    }

    return result;
}

retrieveToken.code:
function retrieveToken(pages, id) {
	for(let i in pages){
		let page = pages[i];
		if(page.id === id){
			return page.access_token
		}
	}
}

buildSendParams.code:
function buildSendParams(parameters) {
	if(parameters.message.quick_replies.length === 0){
		delete parameters.message.quick_replies
	}
	if(parameters.message.message_content){
		delete parameters.message.message_content
	}
	if(parameters.recipient.name.first_name === null && parameters.recipient.name.last_name === null) {
		delete parameters.recipient.name
	}
	if(parameters.recipient.phone_number === null) {
		delete parameters.recipient.phone_number
	}
	if(parameters.recipient.user_ref === null) {
		delete parameters.recipient.user_ref
	}
	if(parameters.message.attachment){
		if(parameters.message.attachment.upload_method){
			delete parameters.message.attachment.upload_method
		}
		if(parameters.message.attachment.type === "template"){
			parameters.message.attachment.payload = JSON.parse(parameters.message.attachment.payload)
		}
	}
	return parameters
}
