formatErrors.code:
function formatErrors(body) {
    let result;
    if (body && Array.isArray(body.errors)) {
        body.errors.forEach((err => {
            if (err.extensions.code)
                result = err.extensions.code + ': ';
            result += err.message;
            if (err.message.includes('Cannot read property'))
                result = 'Not found.';
            // if (err.locations)
            //     result += '\nlocations: ' + err.locations.map((l) => `line ${l.line}, column ${l.column}`).join(' | ');
            if (Array.isArray(err.extensions?.metadata?.fields))
                result += '\n\nError details:\n' + err.extensions.metadata.fields.map((f) => `\tField ${f.name}: ${f.message}`).join('\n\n');
        }));
        return result;
    } else {
        return 'Unknown error, please try again.'
    }
}

