formatOutput.code:
function formatOutput(body) {
    if (body) {
        if (body.data && body.data.transcript) {
            return body.data.transcript;
        };
        if (Array.isArray(body.errors) && body.errors.length > 0) {
            body.errors.forEach((err => {
                if (err.message.includes('Cannot read property')) {
                    return {
                        "result": "Not found."
                    }
                }
            }));
        }; 
    } else {
        return null;
    }
}

