it('Generates the query based on mapped parameters.', () => {
	const parameters = {
		host_email: "host@make.com",
		date: 1743507780,
		title: "Test",
		participant_email: "participant@make.com",
		organizer_email: "organizer@make.com",
		user_id: "123"
	};

	const output = `
    {
        transcripts(
            limit: 50,
            skip: 0,
            title: "Test", 
            host_email: "host@make.com",
            organizer_email: "organizer@make.com",
            user_id: "123",
            date: 1743507780,
            
            
            participant_email : "participant@make.com"
        ){
            id,
            title,
            host_email,
            organizer_email,
            fireflies_users,
            participants,
            date,
            transcript_url,
            duration,
            sentences{text,index,raw_text,start_time,end_time,speaker_id,speaker_name},
            user{user_id,email,name,num_transcripts,recent_transcript,recent_meeting,minutes_consumed,is_admin,integrations}
        }
    }`;

	assert.deepStrictEqual(formatTranscriptsQuery(parameters), output);
});
