    {
        "url": "/c/{{parameters._spolecnost}}/{{parameters._evidence}}",
        "body": {
            "winstrom": "{{wrapAttachment(parameters)}}"
        },
        "method": "POST",
        "response": {
            "output": "{{body}}"
        },
        "condition": "{{parameters.typK == 'typPrilohy.odkaz' && parameters._evidence != 'smlouva-polozka' && parameters._evidence != 'dodavatelska-smlouva' &&  parameters._evidence != 'smlouva'}}"
    },
    {
        "url": "/c/{{parameters._spolecnost}}/smlouva",
        "body": {
            "winstrom": {
                "smlouva": {
                    "id": "{{parameters.smlouva_id}}",
                    "polozkySmlouvy": {
                        "smlouva-polozka": {
                            "id": "{{parameters.id}}",
                            "prilohy": [
                                {
                                    "nazSoub": "{{parameters.name}}",
                                    "link": "{{parameters.link}}",
                                    "typK": "{{parameters.typK}}",
                                    "contentType": "{{parameters.type}}"
                                }
                            ]
                        }
                    }
                }
            }
        },
        "method": "PUT",
        "response": {
            "output": "{{body}}"
        },
        "condition": "{{parameters.typK == 'typPrilohy.odkaz' && parameters._evidence == 'smlouva-polozka'}}"
    },
    {
        "url": "/c/{{parameters._spolecnost}}/{{parameters._evidence}}.xml",
        "body": "{{wrapAttachmentBase64(parameters)}}",
        "type": "raw",
        "method": "PUT",
        "headers": {
            "Content-Type": "{{parameters.type}}"
        },
        "response": {
            "output": "{{body}}"
        },
        "condition": "{{parameters._evidence == 'dodavatelska-smlouva' ||  parameters._evidence == 'smlouva' && parameters.typK != 'typPrilohy.odkaz' }}"
    },
    {
        "url": "/c/{{parameters._spolecnost}}/{{parameters._evidence}}/{{parameters.id}}/prilohy/new/{{encodeURL(parameters.name)}}",
        "body": "{{parameters.data}}",
        "type": "binary",
        "method": "PUT",
        "headers": {
            "Content-Type": "{{parameters.type}}"
        },
        "response": {
            "output": "{{body}}"
        },
        "condition": "{{parameters.typK != 'typPrilohy.odkaz' && parameters._evidence != 'dodavatelska-smlouva' &&  parameters._evidence != 'smlouva'}}"
    }
]

