function getHookID(hooks, url) {
	if (!Array.isArray(hooks)) return null;
	const hook = hooks.filter(hook => hook.url === url)[0];
	return hook && parseInt(hook.id) || null;
}

sortItems.code:
function sortItems(items, prop) {
	return items.sort((a, b) => {
		if (a[prop] < b[prop])
			return -1;
		if (a[prop] > b[prop])
			return 1;
		return 0;
	})
}

