prepareBody.code:
function prepareBody(parameters, failOnCreate = false) {
	const payload = {};
	const wrapper = {
		'@version': '1.0',
		[parameters._evidence]: [
			payload
		]
	}

	if (failOnCreate) {
		payload['@create'] = 'fail';
	}

	for (let key in parameters) {
		if (key !== '_spolecnost' && key !== '_evidence' && parameters[key] != null) {
			if (key === 'polozkyDokladu') {
				payload[key] = parameters[key].map(item => {
					let payload2 = {};
					for (let key2 in item) {
						if (item[key2] != null) {
							payload2[key2] = item[key2];
						}
					}
					return payload2;
				});
			} else if (Buffer.isBuffer(parameters[key])) {
				payload[key] = parameters[key].toString('base64');
			} else {
				payload[key] = parameters[key];
			}
		}
	}
	return wrapper
}

