function prepareBundle(bundle) {
	const numeric = /^-?(?:0(?:\.\d+)?|[1-9]\d*(?:\.\d+)?)$/;
	const datetime = /^\d{4}-\d{2}-\d{2}[+-]\d{2}:\d{2}$/;

	if(bundle.lastUpdate) bundle.lastUpdate = iml.parseDate(bundle.lastUpdate, 'YYYY-MM-DDTHH:mm:ss.SSSZZ');

	for (let name in bundle) {
		if (bundle[name] === 'null' || bundle[name] === '') {
			bundle[name] = null;
		} else if (numeric.test(bundle[name])) {
			bundle[name] = parseFloat(bundle[name]);
		} else if (datetime.test(bundle[name])) {
			bundle[name] = iml.parseDate(bundle[name], 'YYYY-MM-DDZZ');
		} else if (name === 'content' && bundle['content@encoding'] === 'base64') {
			bundle[name] = Buffer.from(bundle[name], 'base64');
		}
	}
	return bundle;
}

