transformProperty.code:
function transformProperty(items, item, edit) {
	const TYP_DOKL = edit ? false : items.some((i) => i.propertyName === 'typDokl');
	const TYPE = {
		logic: 'boolean',
		string: 'text',
		numeric: 'number',
		datetime: 'date',
		relation: 'select',
		blob: 'buffer'
	}
	const param = {
		name: item.propertyName,
		label: item.name,
		type: TYPE[item.type] || item.type,
		required: edit ? item.propertyName === 'id' : (TYP_DOKL ? item.propertyName === 'typDokl' : item.mandatory === 'true'),
		advanced: item.showToUser === 'false'
	}
	if (param.required && param.advanced) param.advanced = false;
	if (param.name === 'nazSoub') param.semantic = 'file:name';

	switch (item.type) {
		case 'select':
			if (!item.values) {
				param.type = 'text';
				break;
			}
			param.editable = true;
			param.options = item.values.value.map((value) => {
				return {
					label: value.$,
					value: value['@key']
				}
			})
			break;

		case 'relation':
			if (typeof item.url !== 'string') {
				param.type = 'text';
				break;
			}
			const match = item.url.match(/\/([^\/]+)$/);
			if (!match) {
				param.type = 'text';
				break;
			}

			param.editable = {
				enabled: true,
				help: 'Zadejte ID.'
			};
			param.options = 'rpc://NacistPodevidenci?_evidence='+ match[1];
			break;

		case 'string':
			if (item.maxLength) {
				param.validate = param.validate || {};
				param.validate.max = parseInt(item.maxLength, 10);
			}
			break;

		case 'date':
			param.time = false;
			break;

		case 'blob':
			param.semantic = 'file:data';
			break;
	}
	return param;
}
