getError.code:
function getError(body, statusCode) {
    if (!body || !body.error) {
		return `[${statusCode}] Something wrong. Please try again.`;
	}
	const { code, details } = body.error;
	let errMessage = `[${code}] `;
	if (details && Array.isArray(details)) {
		details.forEach(err => {
			if (err.errors) {
				err.errors.forEach(({message}) => {
					errMessage +=  `${message}\n`;
				});
			}
		});
	}
	return errMessage;
}

