filterItems.code:
function filterItems(array) {
    return array?.filter(item => item !== null && item !== undefined && item !== "")
}

filterItems.test:
it('filters out null, undefined, and empty strings', () => {
    const input = [1, null, 'valid', undefined, '', 'anotherValid'];
    assert.deepEqual(filterItems(input), [1, 'valid', 'anotherValid']);
});

it('returns the same array if all elements are valid', () => {
    const input = [1, 'valid', 2, 'anotherValid'];
    assert.deepEqual(filterItems(input), input);
});

it('returns an empty array if all elements are invalid', () => {
    const input = [null, undefined, ''];
    assert.deepEqual(filterItems(input), []);
});

