    assert.deepEqual(filterItems(undefined), undefined);
});

formatCustomQuery.code:
function formatCustomQuery(query) {
    return query
            .replaceAll(/[\r\t\n]/g, "")
            .replaceAll("WHERE", " WHERE")
            .replaceAll("ORDER BY", " ORDER BY")
            .replaceAll("FROM", " FROM")
            .replaceAll("LIMIT", " LIMIT")
            .replaceAll("PARAMETERS", " PARAMETERS")
            .trim();
}

