    const expected = "SELECT segments.date, customer.id, customer.descriptive_name, metrics.impressions, metrics.engagements,metrics.clicks, metrics.cost_micros,metrics.conversions, metrics.conversions_value, metrics.all_conversions, metrics.all_conversions_value,metrics.search_impression_share,metrics.search_exact_match_impression_share,metrics.video_views,metrics.view_through_conversions FROM customer WHERE segments.date BETWEEN '2024-04-01' AND '2024-04-10' ORDER BY segments.date DESC";
