    const costMicros = item?.metrics?.costMicros || 0;
    return {
        dimensions: {
            date: item?.segments?.date,
            accountId: item?.customer?.id,
            accountName: item?.customer?.descriptiveName
        },
        metrics: {
            impressions: 0,
            engagements: 0,
            clicks: 0,
            costMicros: 0,
            cost: costMicros/1000000,
            conversions: 0,
            conversionsValue: 0,
