formatCustomQueryOutput.code:
function formatCustomQueryOutput(obj, parentKey = '') {
    return Object.entries(obj).reduce((acc, [key, value]) => {
        const newKey = parentKey ? `${parentKey}_${key}` : key;
        if (typeof value === 'object' && value !== null) {
            Object.assign(acc, formatCustomQueryOutput(value, newKey));
        } else {
            acc[newKey] = value;
        }
        return acc;
    }, {});
}

