extractMessageInfo.code:
function extractMessageInfo(messageBody, verificationToken) {
	if (!messageBody) return {}

	var obj = {}

	obj.verificationTokenMatched = verificationToken ? (verificationToken === messageBody.token) : true;
	obj.messageType = messageBody.type;
	obj.messageText = messageBody.message ? ((messageBody.space.type !== "DM") ? iml.removeReceiverNameFromMsgStart(messageBody.message.text) : messageBody.message.text) : "";
	obj.user = messageBody.user;

	obj.space = messageBody.space;
	if (obj.space.type === "DM") {
		obj.space.displayName = "Direct Messaging";
	}

	obj.eventTime = value = iml.parseDate(messageBody.eventTime);

	return obj;
}

removeReceiverNameFromMsgStart.code:
function removeReceiverNameFromMsgStart(message) {
	if (!message) return;

	let words = iml.split(message, ' ');

	if (!words[0]) return;

	if (words[0].charAt(0) === '@') {
		words.shift();
	}

	return words.join(' ');
}
