    "url": "/calendars/{{encodeURL(parameters.calendar)}}/events",
    "encodeUrl": false,
    "method": "POST",
    "qs": {
        "conferenceDataVersion": 1,
        "sendUpdates": "{{parameters.sendUpdates}}"
    },
    "body": {
        "colorId": "{{parameters.colorId}}",
        "summary": "{{parameters.summary}}",
        "start": "{{dateParameter(parameters.start, parameters.allDayEvent, environment.timezone, parameters.recurrence)}}",
        "end": "{{dateParameter(parameters.end, parameters.allDayEvent, environment.timezone, parameters.recurrence)}}",
        "description": "{{parameters.description}}",
        "location": "{{parameters.location}}",
        "reminders": "{{remindersParameter(parameters.overrides, parameters.useDefault)}}",
        "attendees": "{{parameters.attendees}}",
        "transparency": "{{parameters.transparency}}",
        "visibility": "{{parameters.visibility}}",
        "guestsCanModify": "{{parameters.guestsCanModify}}",
        "recurrence": "{{parameters.recurrence}}",
        "conferenceData": {
            "createRequest": {
                "requestId": "{{uuid}}",
                "conferenceSolutionKey": {
                    "type": "hangoutsMeet"
                },
                "status": {
                    "statusCode": "success"
                }
            },
            "entryPoints": [
                {
                    "entryPointType": "video"
                },
                {
                    "entryPointType": "more"
                },
                {
                    "entryPointType": "phone"
                }
            ],
            "conferenceSolution": {
                "key": {
                    "type": "hangoutsMeet"
                },
                "name": "Google Meet"
            }
        }
    },
    "response": {
        "output": "{{eventResponse(body)}}"
    }
}

