google-meet.apiJsonc:
{
    "authorize": {
        "qs": {
            "scope": "{{join(distinct(merge(oauth.scope, ifempty(parameters.scopes, emptyarray))), ' ')}}",
            "client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
            "redirect_uri": "{{oauth.redirectUri}}",
            "access_type": "offline",
            "response_type": "code",
            "include_granted_scopes": true,
            "prompt": "consent"
        },
        "url": "https://accounts.google.com/o/oauth2/v2/auth",
        "response": {
            "temp": {
                "code": "{{query.code}}"
            }
        }
    },
    "token": {
        "url": "https://accounts.google.com/o/oauth2/token",
        "method": "POST",
        "body": {
            "code": "{{temp.code}}",
            "client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
            "grant_type": "authorization_code",
            "redirect_uri": "{{oauth.redirectUri}}",
            "client_secret": "{{ifempty(parameters.clientSecret, common.clientSecret)}}"
        },
        "type": "urlencoded",
        "response": {
            "data": {
                "expires": "{{addSeconds(now, body.expires_in)}}",
                "accessToken": "{{body.access_token}}",
                "refreshToken": "{{body.refresh_token}}"
            }
        },
        "log": {
            "sanitize": [
                "request.body.code",
                "request.body.client_secret",
                "response.body.access_token",
                "response.body.refresh_token"
            ]
        }
    },
    "refresh": {
        "condition": "{{data.expires < addMinutes(now, 1)}}",
        "url": "https://accounts.google.com/o/oauth2/token",
        "method": "POST",
        "body": {
            "client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
            "grant_type": "refresh_token",
            "client_secret": "{{ifempty(parameters.clientSecret, common.clientSecret)}}",
            "refresh_token": "{{data.refreshToken}}"
        },
        "type": "urlencoded",
        "response": {
            "data": {
                "expires": "{{addSeconds(now, body.expires_in)}}",
                "accessToken": "{{body.access_token}}",
                "refreshToken": "{{body.refresh_token}}"
            },
            "error": {
                "message": "[{{statusCode}}] {{body.error_description}}"
            }
        },
        "log": {
            "sanitize": [
                "request.body.client_secret",
                "request.body.refresh_token",
                "response.body.access_token",
                "response.body.refresh_token"
            ]
        }
    },
    "info": {
        "url": "https://www.googleapis.com/oauth2/v2/userinfo",
        "headers": {
            "Authorization": "Bearer {{connection.accessToken}}"
        },
        "response": {
            "uid": "{{body.id}}",
            "metadata": {
                "type": "email",
                "value": "{{body.email}}"
            },
            "error": {
                "message": "[{{statusCode}}] {{body.error.message}}"
            }
        },
        "log": {
            "sanitize": [
                "request.headers.authorization"
            ]
        }
    },
    "invalidate": {
        "url": "https://accounts.google.com/o/oauth2/revoke",
        "headers": {
            "Authorization": "Bearer {{connection.accessToken}}"
        },
        "method": "POST",
        "qs": {
            "token": "{{connection.accessToken}}"
        }
    }
}

dateParameter.code:
function dateParameter(date, allDay, timezone, recurrence) {

    if(!date) {
        return ;
    }
    if(allDay) {
        return {
            date: iml.formatDate(date, 'YYYY-MM-DD'),
            timeZone: timezone,
            dateTime: null
        };
    }
    else if (recurrence || iml.length(recurrence) > 0) {
        return {
            dateTime: iml.formatDate(date, 'YYYY-MM-DDTHH:mm:ssZ'),
            timeZone: timezone,
            date: null
        };
    }
    else {
        return {
            dateTime: iml.formatDate(date, 'YYYY-MM-DDTHH:mm:ssZ'),
            date: null
        };
    }
}

eventResponse.code:
function eventResponse(body) {

    if (Array.isArray(body)) {
        body.forEach(item => {
            if(item.start && item.end) {
                if (item.start.date && item.end.date) {
                    item.start = iml.parseDate(item.start.date);
                    item.end = iml.parseDate(item.end.date);
                }
                else if (item.start.dateTime && item.end.dateTime) {
                    item.start = iml.parseDate(item.start.dateTime);
                    item.end = iml.parseDate(item.end.dateTime);
                }
            }
        });
    }
    else {
        if(body.start && body.end) {
            if (body.start.date && body.end.date) {
                body.start = iml.parseDate(body.start.date);
                body.end = iml.parseDate(body.end.date);
            }
            else if (body.start.dateTime && body.end.dateTime) {
                body.start = iml.parseDate(body.start.dateTime);
                body.end = iml.parseDate(body.end.dateTime);
            }
        }
    }

    return body;
}

colorRpcResponse.code:
function colorRpcResponse(body, calendar) {

    if (calendar) {
        var arr = Object.values(body.calendar);
        for (i = 0; i < iml.length(arr); i++) {
            arr[i].id = i + 1;
        }
        return arr;
    }
    else {
        var arr = Object.values(body.event);
        for (i = 0; i < iml.length(arr); i++) {
            arr[i].id = i + 1;
        }
        return arr;
    }

}

remindersParameter.code:
function remindersParameter(overrides, useDefault) {

    if(overrides || iml.length(overrides)>0) {
        return {
            overrides: overrides,
            useDefault: false
        };
    }
    else if (useDefault && !overrides) {
        return {
            useDefault: useDefault
        }
    }
    else return undefined;
}
