listTasks.apiJsonc:
{
    "url": "/lists/{{parameters.tasklist}}/tasks",
    "method": "GET",
    "qs": {
        "showCompleted": "{{if(parameters.showNoCompleted, false, true)}}",
        "showHidden": "{{if(parameters.showNoCompleted, false, true)}}",
        "maxResults": 100
    },
    "response": {
        "iterate": "{{body.items}}",
        "output": {
            "label": "{{if(item.parent, 'Sub: ', '') + item.title + if(item.status === 'completed', ' (Completed)', '')}}",
            "value": "{{item.id}}"
        },
        "limit": 500
    },
    "pagination": {
        "qs": {
            "pageToken": "{{body.nextPageToken}}"
        },
        "condition": "{{ifempty(body.nextPageToken, false)}}"
    }
}

