watchTasks.apiJsonc:
{
    "url": "/lists/{{parameters.tasklist}}/tasks",
    "method": "GET",
    "qs": {
        "completedMin": "{{if(parameters.select === 'complete', parseDate('2000-01-01'), undefined)}}",
        "dueMin": "{{if(parameters.select === 'due', parseDate('2000-01-01'), undefined)}}",
        "showCompleted": "{{if(parameters.select === 'complete' || parameters.showCompleted, true, false)}}",
        "showHidden": "{{if(parameters.select === 'complete' || parameters.showCompleted, true, false)}}",
        "maxResults": 100
    },
    "response": {
        "iterate": "{{body.items}}",
        "trigger": {
            "id": "{{item.id}}",
            "date": "{{switch(parameters.select, 'update', item.updated, 'due', item.due, 'complete', item.completed)}}",
            "type": "date",
            "order": "desc"
        },
        "output": "{{item}}",
        "limit": "{{parameters.limit}}"
    },
    "pagination": {
        "qs": {
            "pageToken": "{{body.nextPageToken}}"
        },
        "condition": "{{ifempty(body.nextPageToken, false)}}"
    }
}

