processValidationErrors.code:
function processValidationErrors(validationErrors) {
    if (validationErrors === undefined || validationErrors == null) {
        return
    }
    let output = "\r\nError details: \r\n"
    validationErrors.forEach(validationError => {
        output += `- On field '${validationError.value}' at property '${validationError.property}': ${validationError.message}\r\n`
    })
    return output
}
