transformDate.code:
function transformDate(body) {
	if (body.accession_date) {body.accession_date = new Date(body.accession_date);}
	if (body.request_date) {body.request_date = new Date(body.request_date);}
	if (body.approved_date) {body.approved_date = new Date(body.approved_date);}
	if (body.creation_date) {body.creation_date = iml.parseDate(body.creation_date);}
	if (body.date_last_recurrence) {body.date_last_recurrence = iml.parseDate(body.date_last_recurrence);}
	if (body.date_next_charge) {body.date_next_charge = iml.parseDate(body.date_next_charge);}
	if (body.activation_date) {body.activation_date = iml.parseDate(body.activation_date);}

	return body;
}

transformBasicHeader.code:
function transformBasicHeader(basicHeader) {
	if (basicHeader.substring(0, 6) === 'Basic '){
		return basicHeader.substring(6, basicHeader.length);
	} else return basicHeader;
}
