parseResponse.code:
function parseResponse(body) {
	if (!Array.isArray(body.MIRecord) || !body.Metadata || !Array.isArray(body.Metadata.Field)) return undefined;

	const fieldDefinition = iml.toCollection(body.Metadata.Field, '@name', '@type');

	return body.MIRecord.map(record => {
	if (!Array.isArray(record.NameValue)) return null;
		return record.NameValue.reduce((acc, val) => {
			let value = String(val.Value).trim();
			switch (fieldDefinition[val.Name]) {
				case "N":
					acc[val.Name] = Number(value);
					break;
				case "D":
					acc[val.Name] = value !== "" ? iml.parseDate(value, "YYYYMMDD") : null;
					break;
				default:
					acc[val.Name] = value; 
			}
			return acc;
		}, {});
	}).filter(record => typeof record === "object" && record !== null);
}

prepareParameters.code:
function prepareParameters(parameters, paramsType) {
	Object.entries(parameters).forEach(([k, v]) => {
		if (paramsType[k] === "date") {
			parameters[k] = iml.formatDate(v, "YYYY-MM-DD");
		}
		if (paramsType[k] === "boolean") {
			parameters[k] = v ? 1 : 0;
		}
	});
	return parameters;
}
