formatMultipleLeadsRequest.code:
function formatMultipleLeadsRequest(leadsArr) {
    if (!leadsArr) return undefined;

    if (!Array.isArray(leadsArr)) return leadsArr;

    leadsArr.forEach((element) => {
        if (!element.custom_variables || !Array.isArray(element.custom_variables)) return;
        element.custom_variables = iml.toCollection(element.custom_variables, 'key', 'value');
    });

    return leadsArr;
}

formatMultipleLeadsRequest.test:
it('should return undefined if input does not exist or false', () => {
    assert.strictEqual(formatMultipleLeadsRequest(undefined), undefined);
    assert.strictEqual(formatMultipleLeadsRequest(null), undefined);
    assert.strictEqual(formatMultipleLeadsRequest(false), undefined);
    assert.strictEqual(formatMultipleLeadsRequest(''), undefined);
});

it('should return leadsArr if input is not an array', () => {
    assert.deepStrictEqual(formatMultipleLeadsRequest({ key: 'notAnArray' }), { key: 'notAnArray' });
    assert.strictEqual(formatMultipleLeadsRequest(true), true);
    assert.strictEqual(formatMultipleLeadsRequest(123), 123);
    assert.strictEqual(formatMultipleLeadsRequest('notAnArray'), 'notAnArray');
    assert.deepStrictEqual(formatMultipleLeadsRequest({}), {});
});

it('should not change input array if input is correct lead array without custom fields', () => {
    const input = [
        {
            email: "temp@mail.me",
            first_name: "Test1"
        },
        {
            email: "temp1@mail.me",
            first_name: "Test2"
        }
    ]

    const exptectedOutput = [
        {
            email: "temp@mail.me",
            first_name: "Test1"
        },
        {
            email: "temp1@mail.me",
            first_name: "Test2"
        }
    ]

    assert.deepStrictEqual(formatMultipleLeadsRequest(input), exptectedOutput);
});

it('should return formatted output if input is correct lead array with custom fields', () => {
    const input = [
        {
            email: "temp@mail.me",
            custom_variables: [
                {
                    key: "testKey2",
                    value: "testVal1"
                }
            ]
        },
        {
            email: "temp1@mail.me",
            custom_variables: [
                {
                    key: "testKey2",
                    value: "testVal2"
                }
            ]
        }
    ]

    const exptectedOutput = [
        {
            email: "temp@mail.me",
            custom_variables: {
                testKey2: "testVal1"
            }
        },
        {
            email: "temp1@mail.me",
            custom_variables: {
                testKey2: "testVal2"
            }
        }
    ]

    assert.deepStrictEqual(formatMultipleLeadsRequest(input), exptectedOutput);
});
