function buildFilters(filters, timezone, match) {
  if(!filters) return;
	const rules = []
    ;(filters || []).forEach(filter => {
      // "a":"Id","b":"1","o":"text:equals"
      filter.forEach(cond => {
        const [, operator] = cond.o.split(':')
        switch (cond.o) {
          case 'text:is':
          case 'text:is not':
          case 'text:contains':
          case 'text:does not contain':
          case 'text:starts with':
          case 'text:ends with':
          case 'text:is any':
          case 'text:near': {
            rules.push({
              field: cond.a,
              operator,
              value: cond.b
            })
            break
          }
		  case 'number:is':
          case 'number:is not':
          case 'number:higher than':
          case 'number:lower than':
          case 'number:is blank':
          case 'number:is not blank': {
