          case 'date:is blank':
          case 'date:is not blank':
          case 'date:is today':
          case 'date:is today or before':
          case 'date:is today or after':
          case 'date:is before today':
          case 'date:is after today':
          case 'date:is before current time':
          case 'date:is after current time': {
            rules.push({
              field: cond.a,
              operator
            })
            break
          }
          case 'date:is':
          case 'date:is not':
          case 'date:is before':
          case 'date:is after': {
		  	rules.push({
              field: cond.a,
              operator,
              value: cond.b
            })
            break
          }
        }
      })
    })
	return JSON.stringify({
        match: match ? match: 'and',
        rules
    })
}

