function getRecordInput(parameters) {
	// backward compatibility, dates
	const dates = {};
	Object.keys(parameters)
		.filter(param => param.endsWith('_datetype') && parameters[param] !== 'simple')
		.forEach(param => {
			dates[param.substr(0, param.length - '_datetype'.length)] = parameters[param]
		});

	const body = {};
	Object.keys(parameters)
		.filter(param => param !== 'object_id' && !param.endsWith('_datetype'))
		.forEach(param => {
			const value = parameters[param];
			if (Array.isArray(value)) {
				// if multiple select is a one selection send it as a string
				body[param] = value.length == 1 ? value.pop() : value;
				//body[param] = value;

			} else if (value !== null && typeof value === 'object' && Object.keys(value).length === 0) {
				// remove empty object from request
			} else if (dates[param]) {
				if (dates[param] === 'fromTo') {
					body[param] = {
						date: value.from,
						to: {
							date: value.to
						}
					};
				} else if (dates[param] === 'repeat') {
					const spec = {
						date: value.from
					};
					if (value.to) {
						spec.to = value.to;
					}
					if (value.repeat) {
						spec.repeat = value.repeat;
					}
					body[param] = spec;
				}
			} else {
				body[param] = value;
			}
		});

	return body;
}

