function getErrorMessage(code, body) {
	let message = null
	if (!body) {
		message = 'Error'
	} else if (typeof body === 'string') {
		message = body
	} else if (typeof body === 'object') {
		if (body.error) {
			message = body.error
		} else {
			message = JSON.stringify(body)
		}
	}
	return `${code}: ${message}`
}

