function getRecordOutput(body, mode = 'both') {
  if (typeof body !== 'object')
    throw TypeError('"body" is expected to be an object.');
  if (typeof mode !== 'string')
    throw TypeError('"mode" is expected to be a string.');

  return Object.entries(body).reduce((acc, [key, value]) => {
    if (key.startsWith('field_')) {
      const valueToReturn =
        typeof value === 'string' && value === '' ? null : value;

      if (mode === 'raw' && !key.endsWith('_raw')) {
        return acc;
      }
      if (mode === 'formatted' && key.endsWith('_raw')) {
        return acc;
      }
      return { ...acc, [key]: valueToReturn };
    }

    // leave this condition after refactoring just in case it was placed here intentionally
    // and some of the fields that don't meet the conditions should be skipped
    if (key === 'id') {
      return { ...acc, [key]: value };
    }

    return acc;
  }, {});
}

